import React, { useContext } from 'react'
import { Link, NavLink } from 'react-router-dom'
import { CurrentUserContext } from 'contexts/currentUser'

function TopBar() {
  const [currentUserSate] = useContext(CurrentUserContext)

  return (
    <nav className='navbar navbar-light'>
      <div className='container'>
        <Link to='/' className='navbar-brand'>
          JS EDU
        </Link>
        <ul className='nav navbar-nav pull-xs-right'>
          <li className='nav-item'>
            <NavLink to='/' className='nav-link'>
              Home
            </NavLink>
          </li>
          {currentUserSate.isLoggedIn === false && (
            <>
              <li className='nav-item'>
                <NavLink to='/login' className='nav-link'>
                  Sign in
                </NavLink>
              </li>
              <li className='nav-item'>
                <NavLink to='/register' className='nav-link'>
                  Sign up
                </NavLink>
              </li>
            </>
          )}

          {currentUserSate.isLoggedIn && (
            <>
              <li className='nav-item'>
                <NavLink to='/articles/new' className='nav-link'>
                  <i className='ion-compose'></i>
                  &nbsp; New Post
                </NavLink>
              </li>
              <li className='nav-item'>
                <NavLink
                  to={`/profiles/${currentUserSate.currentUser.username}`}
                  className='nav-link'
                >
                  <img
                    className='user-pic'
                    src={currentUserSate.currentUser.image}
                    alt=''
                  />
                  &nbsp; {currentUserSate.currentUser.username}
                </NavLink>
              </li>
            </>
          )}

        </ul>
      </div>
    </nav>
  )
}

export default TopBar